import { motion } from "framer-motion";
import React from "react";
import logo from '../../img/SillyCoder.png';

//setup the variables
var token_type = "empty";
var access_token = "empty";
var url = "http://sillycode.tech/dashboard";

function Dashboard() {
    

    window.addEventListener('load', load);

    const loginclick = () => {
        if (window.location.href.toString() != url) {
            window.location.href = url;
        } else {
            window.location.href = "https://discord.com/api/oauth2/authorize?client_id=1043950277738385499&redirect_uri=http%3A%2F%2Fsillycode.tech%2Fdashboard&response_type=code&scope=identify%20guilds";    
        }
    }

    return (
        <body>
            <nav>
                <div className='logoNav'>
                    
                    <motion.a
                    href='index'
                    whileHover={{scale: 1.1}}
                    whileTap={{scale: 0.9}}
                    ><img className='round' alt='' src={logo} width="150px" height="150px" ></img>
                    </motion.a>
                    
                    <motion.button
                    whileHover={{scale: 1.1}}
                    whileTap={{scale: 0.9}}
                    className='indexBtn' 
                    id="loginButton"
                    onClick={loginclick}
                    >Login</motion.button>  
                     
                </div>   
            </nav>
            <br/>

            <main id="content"><span className="middle">Nothing to Display. Please Login.</span></main>

        </body>
);

}

function load() {
    if (window.location.href.toString() != url) {
            
    //get the url
    let url = document.URL;

    //split the url in the terms we need
    const split = url.split("=");
    const split2 = split[1].split("&");
    const split3 = split[2].split("&");

    //order the tokens their content
    token_type = split2[0];
    access_token = split3[0];
    
    //go with the token and token type to the Discordapi
    fetch('https://discord.com/api/users/@me', {
    headers: {
    authorization: `${token_type} ${access_token}`,
    },
    })
    .then(result => result.json())
    .then(response => {
        //give the build controller the data from the Api after asking for a cookie
        buildController(response);
    })
    .catch(console.error); 
    
    } else {
        //write that we are not able to use the dashboard because we are loged in
        document.getElementById("content").innerHTML="Login to use the Dashboard";
        document.getElementById("content").className = "middle";
    }
}    

function buildController(response) {
    //collect the things we become from the api
    const {id, username, avatar ,discriminator} = response;

    //clear content
    clearContent();

    //Start to build the ui
    document.querySelector("body").innerText = "Welcome " + username + " #"  + discriminator + " to our Dashboard which is not avaible at the Moment."
}

function clearContent() {
    document.querySelector("nav").remove();
    document.querySelector("main").remove();
    document.querySelector("br").remove();
}

export default Dashboard;
