require('dotenv').config();
import { Client } from "discord.js";

const client = new Client({intents: []});
client.login(BotData.env.BotToken).then(() => {
    console.log(`bot logged in as ${client.user.username}`);
    client.user.setActivity("https://www.sillycode.tech", { type: 'WATCHING' });  
}).catch((err) => console.log(err))

function checkRole(userid, rolename){
    let usercheck = client.users.fetch(userid);
    if (usercheck.roles.cache.some(r=>[rolename].includes(r.name)) ) {
        // has one of the roles
        return true;
      }
      
      else {
        // has none of the roles
        return false;
      }
}

export default checkRole;
