import "../../css/App.css";
import React, {useEffect} from "react";
import GoBack from "../componets/GoHome";
import MenuBar from "../componets/MenuBar";
import { isAndroid, isIOS, isMobile} from "react-device-detect";
import addContent from "./contentCreator";

//vars
var content = [];
function SoftPage() {
  const [refresh, setRefresh] = React.useState(0);
  var filters = [<></>,<></>,<></>];
  
  // get order
  var order = getOrder();

  //create content and create filter
  for (var i = 0; i < order.length; i++) {
    filters[i] = (
      <>
        <input type="checkbox" onClick={() => {update(); setRefresh(refresh + 1)}} id={"show" + order[i]} />
        <label htmlFor={"show" + order[i]}>{order[i]}</label>
      </>);
    if(i = 0){
      filters[i] = filters[i].repleace("onClick", "defaultChecked={true} onClick");
    }
  }
 

  useEffect(() => {
    if (refresh == 0) {
      update()
      setRefresh(refresh +1)
    }
  });
  
 
}

//ANCHOR Update
function update() {
  content = [];
  // witch platform
  var plattform = "Desktop";
  if(isAndroid){
   plattform = "Android"
  } else if(isIOS){
    plattform = "Ios"
  }

  //get order
  var order = getOrder();

  //create content and create filter
  var filter = [false, false, false]
  for (var i = 0; i < order.length; i++) {
    filter[i] = getChecked("show" + order[i]);
    console.log(order);
    if(filter[i]){
      content[i] = addContent(order[i])
    }
  }
 
  //add lines between
  if(filter[0] && filter[1]){
    plattform[0] = <>{plattform[0]} <hr/></>
  }

  if(filter[1] && filter[2]){
    plattform[1] = <>{plattform[1]} <hr/></>
  }
}

function getChecked(id) {
  try {
    return document.getElementById(id).checked;
  } catch (error) {
    console.warn(error)
  }

}

function getOrder(){
   // witch platform
   var plattform = "Desktop";
   if(isAndroid){
    plattform = "Android"
   } else if(isIOS){
     plattform = "Ios"
   }
 
   //Get the oder
   switch(plattform){
     case "Desktop":
      return [plattform,"Android","Ios" ]
 
     case "Android":
      return [plattform,"Desktop", "Ios"]
 
     case "Ios":
      return [plattform,"Android","Desktop" ]
   }
}


export default SoftPage;
